#include <stdio.h>
#include <stdlib.h>
#include <string.h>

unsigned long long int store[1001];
int maxI = 0;

void temp(char * n);
unsigned long long int multiply(int arr[13]);
unsigned long long int max();
void prints(char s[1001]);

int main(void) {
  
  char num[1001] = "";
  printf("%s\n\n", num);

  temp(num);
  printf("%llu\t\t\t\t%d\n", max(), maxI);

  prints(num);

  return 0;
}

void prints(char s[1001]) {
  for(int i = maxI; i < maxI + 13; i++) {
    printf("%c", s[i]);

  } printf("\n");
}

void temp(char * n) {
  char a[1001];
  strcpy(a, n);

  for(int i = 0, j = 12; j < 1000; i++, j++) { // parse through to create 13 digit arr
    int t[13];
    for(int k = i, m = 0; k <= j; k++, m++) {
      t[m] = a[k] - '0';
      //printf("%d\n", t[m]);
      store[i] = multiply(t); // store product
    }
  }
}

unsigned long long int multiply(int arr[13]) {
  unsigned long long int n = 1;
  for(int i = 0; i < 13; i++) {
    n *= arr[i];
  }
  //printf("%llu\n", n);
  return n;
}

unsigned long long int max() {
  unsigned long long int max = store[0];
  for(int i = 0; i < 1000; i++) {
    if(store[i] > max) {
      max = store[i];
      maxI = i;
      printf("%llu\t\t\t\t%d\n", max, maxI);
    }
  }
  return max;
}
